import React from "react";

const selectDestiny = (ev) => {
  // const citySelect = ev.target.value;
  const citySelect = ev.currentTarget.value;
  alert(`!Tu destino es ${this.props.value} !`);
  console.log(citySelect);
};

class Destiny extends React.Component {
  constructor(props) {
    super(props);
    this.props = "Buenos Aires";
    this.props = "Sidney";
    this.props = "Praga";
    this.props = "Tokio";
  }
  render() {
    return (
      <>
        <p>Elige tu destino</p>
        <select name="select" onChange={selectDestiny} id={this.props}>
          <option value="value1">Buenos Aires</option>
          <option value="value2">Sidney</option>
          <option value="value3">Praga</option>
          <option value="value4">Tokio</option>
        </select>
      </>
    );
  }
}

export default Destiny;
