import React from "react";
import "./App.css";

class OnionHater extends React.Component {
  constructor() {
    super();
    this.handleOnion = this.handleOnion.bind(this);
    this.isHating = false;
  }

  handleOnion(ev) {
    if (ev.currenTarget.value.includes("cebolla")) {
      this.isHating = this.forceUpdate("inputText");
    }
  }

  render() {
    return (
      <main>
        <textarea
          onChange={this.handleOnion}
          onChange={this.isHating}
        ></textarea>
      </main>
    );
  }
}

export default OnionHater;
