import React from "react";

// const selectValue = document.querySelector(".select");
const selectDestiny = (ev) => {
  ev.currentTarget.value = alert(`!Tu destino es ${this} !`);
};

class Destiny extends React.Component {
  render() {
    return (
      <select name="select" onChange={selectDestiny}>
        <option value="value1">Buenos Aires</option>
        <option value="value2" selected>
          Sidney
        </option>
        <option value="value3">Praga</option>
        <option value="value3">Tokio</option>
      </select>
    );
  }
}

export default Destiny;
