import React from "react";

// guarda los cambios que se hacenen el input y se los pasa a su madre
function TextInput(props) {
  // constructor(props) {
  //   super(props);
  // }
  const handleKeyUp = (ev) => {
    let inputText = ev.target.value;
    console.log(inputText);
  };

  return (
    <>
      <label htmlFor="mimi">Traductor de MIMIMI</label>
      <form action="POST">
        <textarea
          name="text"
          id="mimi"
          cols="30"
          rows="10"
          onKeyUp={handleKeyUp}
        ></textarea>
      </form>
    </>
  );
}

export default TextInput;
