import React from "react";
import MIMITranslator from "./MIMITranslator";

// guarda los cambios que se hacenen el input y se los pasa a su madre
class TextInput extends React.Component {
  render() {
    return (
      <>
        <label htmlFor="mimi">Traductor de MIMIMI</label>
        <form action="POST">
          <textarea name="tex" id="mimi" cols="30" rows="10"></textarea>
          <MIMITranslator />
        </form>
      </>
    );
  }
}

export default TextInput;
