import "../components/App.css";
import React from "react";
import TextInput from "./TextInput";
import MIMITranslator from "./MIMITranslator";

// guarda los cambios que recibe en un atributo y fuerza el repintado de su nieta >>lifting
class App extends React.Component {
  constructor(props) {
    super(props);
  }

  handleKeyPress(ev) {
    this.inputText = this.inputText.bind(this);
    this.inputText.forceUpdate();
    const inputText = ev.target.value;
    console.log("quee");
  }

  render() {
    console.log("hola");
    this.inputText = "";
    return (
      <div className="App">
        <TextInput onKeyPress={this.handleKeyPress} value={this.inputText} />
        <MIMITranslator />
      </div>
    );
  }
}

export default App;
