import React from "react";

// guarda los cambios que se hacenen el input y se los pasa a su madre
class TextInput extends React.Component {
  handleKeyUp(ev) {
    const inputText = ev.target.value;
    console.log(inputText);
  }

  render() {
    return (
      <>
        <label htmlFor="mimi">Traductor de MIMIMI</label>
        <form action="POST" onKeyUp={this.handleKeyUp}>
          <textarea name="text" id="mimi" cols="30" rows="10"></textarea>
        </form>
      </>
    );
  }
}

export default TextInput;
