import React from "react";
import "./App.scss";
import Form from "./Form";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "",
      description: "",
      language: "",
    };
  }
  render() {
    return (
      <>
        <h1>What's your favorite film?</h1>
        <Form />
        <article>
          <p>Title: </p>
          <p>Description</p>
          <p>Language</p>
        </article>
      </>
    );
  }
}

export default App;
