import React from "react";

class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "",
      description: "",
      language: "",
    };
    this.handleTitle = this.handleTitle.bind(this);
  }

  handleTitle(ev) {
    const inputValue = ev.currentTarget.value;
    this.setState({
      title: inputValue,
      description: inputValue,
      language: inputValue,
    });
  }

  render() {
    return (
      <form action="" onChange={this.handleTitle}>
        <label htmlFor="nameFilm">Title</label>
        <input
          type="text"
          name="nameFilm"
          id="1"
          placeholder="Title"
          value={this.state.title}
        />
        <label htmlFor="description">Description</label>
        <textarea
          name="description"
          id="2"
          cols="30"
          rows="10"
          placeholder="description"
          value={this.state.description}
        ></textarea>
        <label htmlFor="language">Language</label>
        <select name="langage" id="3" value={this.state.language}>
          <option value="value1">English</option>
          <option value="value2">Chinese</option>
          <option value="value4">German</option>
          <option value="value3">Spanish</option>
          <option value="value5">Indian</option>
          <option value="value5">Russian</option>
        </select>
      </form>
    );
  }
}

export default Form;
