import React from "react";

class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "",
      description: "",
      language: "",
    };
    this.handleInput = this.handleInput.bind(this);
  }

  // la misma funcion recoge el valor de cada input por su id y actualiza su estado
  handleInput(ev) {
    const { value, id } = ev.currentTarget;
    console.log(value);
    console.log(this.setState);
    if (id === "title") {
      this.setState({
        title: value,
      });
    } else if (id === "description") {
      this.setState({
        description: value,
      });
    } else if (id === "language") {
      this.setState({
        language: value,
      });
    }
  }

  render() {
    console.log("hola");
    return (
      <React.Fragment>
        <form action="">
          <label htmlFor="name">Title</label>
          <input
            type="text"
            name="title"
            id="title"
            placeholder="Title"
            onChange={this.handleInput}
            value={this.state.title}
          />
          <label htmlFor="description">Description</label>
          <textarea
            name="description"
            id="description"
            cols="30"
            rows="10"
            placeholder="description"
            onChange={this.handleTitle}
          ></textarea>
          <label htmlFor="language">Chose a language</label>
          <select name="language" id="language" onChange={this.handleTitle}>
            <option value="English">English</option>
            <option value="Portuguese">Portuguese</option>
            <option value="Chinese">Chinese</option>
            <option value="German">German</option>
            <option value="Spanish">Spanish</option>
            <option value="Indian">Indian</option>
            <option value="Russian">Russian</option>
          </select>
        </form>

        <article>
          <p>Title: {this.title}</p>
          <p>Description:</p>
          <p>Language:</p>
        </article>
      </React.Fragment>
    );
  }
}

export default Form;
