import React from "react";

class Form extends React.Component {
  render() {
    return (
      <form action="">
        <label htmlFor="nameFilm"></label>
        <input type="text" name="nameFilm" id="1" placeholder="Title" />
        <label htmlFor="description"></label>
        <textarea
          name="description"
          id="2"
          cols="30"
          rows="10"
          placeholder="description"
        ></textarea>
        <label htmlFor="language"></label>
        <input type="select" name="language" id="3" />
      </form>
    );
  }
}

export default Form;
